hudson.plugins.warnings.GroovyParser.script:
import hudson.plugins.warnings.parser.Warning
import hudson.plugins.analysis.util.model.Priority

String fileName = matcher.group(1)
String lineNumber = matcher.group(2)
String category = matcher.group(3)
String warningNumber = matcher.group(4)
String message = matcher.group(5)
String type = "Lintware"

Pri = Priority.LOW

if('E' == category[0]){
    Pri = Priority.HIGH
}else if('W' == category[0]){
    Pri = Priority.NORMAL
}

if(3000>Integer.parseInt(warningNumber)){
    Pri = Priority.HIGH
    category = "Lintware " + category + " "+ warningNumber
    type = "Lintware"
}else if ((3000<=Integer.parseInt(warningNumber)) && (4000>Integer.parseInt(warningNumber))){
    category = "Codeware " + category + " " + warningNumber
    type = "Codeware"
}else if ((4000<=Integer.parseInt(warningNumber))){
    category = "Checkware " + category + " " + warningNumber
    type = "Checkware"
}

return new Warning(fileName, Integer.parseInt(lineNumber), type, category, message, Pri);

import hudson.plugins.warnings.parser.Warning
String category = matcher.group(1) String message = matcher.group(2) String fileName = matcher.group(3) String lineNumber = matcher.group(4)
return new Warning(fileName, Integer.parseInt(lineNumber), "Dynamic Parser", category, message);
