hudson.tasks.BatchFile.command:
call "C:\Program Files (x86)\Microsoft Visual Studio 9.0\VC\vcvarsall.bat" amd64

if exist RM_INSTALL_64 rmdir /s /q RM_INSTALL_64
if not exist RM_BUILD_64 mkdir RM_BUILD_64
cd RM_BUILD_64

if exist *.zip del *.zip

if "%REMOVE_TEST_DIR%" == "ON" (
  rmdir /s /q tests
)

if "%USE_LABELS%" NEQ "OFF" (
  set ALL_TEST=ON
) else (
  set ALL_TEST=OFF
)

REM e:\JENKINS\CMake-2.8\bin\cmake.exe -DGIT_ROOT_DIR:FilePath="e:\JENKINS"

cmake.exe -DCMAKE_BUILD_TYPE=RELEASE -DCMAKE_INSTALL_PREFIX=../RM_INSTALL_64 -DRM_USE_XPRESS=ON -DRM_USE_SYMPHONY=OFF -DRM_XPRESS_DIR:FilePath="c:/xpressmp_64_780" -DRM_ENABLE_DOWNLOAD_PYTHON=ON -DRM_USE_OPENMP=OFF -DRM_USE_THREADS=OFF -DRM_USE_ABC=ON -DRM_ABC_LEVEL=2 -DCPACK_GENERATOR:String="ZIP" -DGIT_BRANCH:String="%GIT_BRANCH%" -DENABLE_OTHER_TEST=%OTHERTEST% -DENABLE_ALL_TEST=%ALL_TEST% -DRM_ENABLE_DOWNLOAD_MPI=OFF -DRM_ENABLE_DOWNLOAD_MPI4PY=OFF -DRM_ENABLE_OPTIM_CAPA=%ACTIVATE_OPTIM_CAPA% -DRM_USE_SYMPHONY=OFF -G "NMake Makefiles" ../RiskManager

nmake

REM Remove the old test directory to avoid cumulative test analysis
rmdir /s /q Testing

REM  Bug ctest: we can't use both -L and -LE flags - fixed in cmake 3.1

if "%DO_TESTS%" == "ON" (
  ctest.exe -C Experimental -T Start
  if "%USE_LABELS%" == "OFF" (
    ctest.exe -j 4 -C Experimental -T Test -R Xpress --no-compress-output
  ) else (
    ctest.exe -j 4 -C Experimental -T Test -R Xpress --no-compress-output -L %USE_LABELS%
  )
  
)

call "C:\Program Files (x86)\Microsoft Visual Studio 9.0\VC\vcvarsall.bat" x86

if exist RM_INSTALL_32 rmdir /s /q RM_INSTALL_32
if not exist RM_BUILD_32 mkdir RM_BUILD_32
cd RM_BUILD_32

if exist *.zip del *.zip

if "%REMOVE_TEST_DIR%" == "ON" (
  rmdir /s /q tests
)

if "%USE_LABELS%" NEQ "OFF" (
  set ALL_TEST=ON
) else (
  set ALL_TEST=OFF
)

cmake.exe -DCMAKE_BUILD_TYPE=RELEASE -DCMAKE_INSTALL_PREFIX=../RM_INSTALL_32 -DRM_USE_XPRESS=ON -DRM_USE_SYMPHONY=OFF -DRM_XPRESS_DIR:FilePath="c:/xpressmp_32_780" -DRM_ENABLE_DOWNLOAD_PYTHON=ON -DRM_USE_OPENMP=OFF -DRM_USE_THREADS=OFF -DRM_USE_ABC=ON -DRM_ABC_LEVEL=2 -DCPACK_GENERATOR:String="ZIP" -DGIT_BRANCH:String="%GIT_BRANCH%" -DENABLE_OTHER_TEST=%OTHERTEST% -DENABLE_ALL_TEST=%ALL_TEST% -DRM_ENABLE_DOWNLOAD_MPI=OFF -DRM_ENABLE_DOWNLOAD_MPI4PY=OFF -DRM_ENABLE_OPTIM_CAPA=%ACTIVATE_OPTIM_CAPA% -DRM_USE_SYMPHONY=OFF -G "NMake Makefiles" ../RiskManager

nmake

REM Remove the old test directory to avoid cumulative test analysis
rmdir /s /q Testing


REM  Bug ctest: we can't use both -L and -LE flags - fixed in cmake 3.1

if "%DO_TESTS%" == "ON" (
  ctest.exe -C Experimental -T Start
  if "%USE_LABELS%" == "OFF" (
    ctest.exe -j 4 -C Experimental -T Test -R Xpress --no-compress-output
  ) else (
    ctest.exe -j 4 -C Experimental -T Test -R Xpress --no-compress-output -L %USE_LABELS%
  )
  
)
