definition.script:
node {
    checkout([
        $class: 'GitSCM',
        branches: [[name: '*/master']],
        doGenerateSubmoduleConfigurations: false,
        extensions: [
            [$class: 'DisableRemotePoll'],
            [$class: 'PathRestriction', excludedRegions: '', includedRegions: 'resources/.*']
        ],
        submoduleCfg: [],
        userRemoteConfigs: [
            [url: 'ssh://me@someserver.com:somerepo.git']
        ]
    ])
    echo 'hello'
}
