definition.script:
stage ('test') {
    def branches = [:]
    def test_jobs = [:]
    def  jobnames = ["HW_run1", "HW_run2", "HW_run3", "HW_run4"]

    node ('master') {
        jobnames.each { jobname -> 
           branches [jobname] = { jj=build job: jobname;
           test_jobs[jobname] = jj.getDuration()
           }
        }

        try {
             parallel branches
        } catch (e) {
            println "Unit test stage failed with error " + e
        }
    }
}
