String propFileURL= 'https://someserver.content/dynamic-test-list.prop'
String key = 'suites'

def propFile = ""
def tries = 3
def successful = false
def timeout = 30000
while(tries > 0 && !successful) {
    try {
        propFile = new URL(propFileURL).getText([connectTimeout: timeout, readTimeout: timeout])
        successful = true
    } catch(e) {
        println("Download failed for ${propFileURL}")
        if (tries <= 1) {
            throw e
        }
        sleep(5000)
    }
    tries = tries - 1
}

def suiteList = []
if(successful) {
    def props = new Properties()
    props.load(new StringReader(propFile))
    suiteList = props[key].split(",")
    suiteList.eachWithIndex { suite, i ->
        suiteList[i] = suiteList[i].trim()
    }
}


suiteList.each() { println "${it}" }
spaceSepSuites = suiteList.join(" ")

def map = [ALL_TESTS: spaceSepSuites]
return map
