definition.script:
pipeline {
    agent none

    stages {
        stage('Hello') {
            steps {
                echo 'Hello World'
            }
        }
        stage('Pause-for-input') {
            steps {
                script {
                    scanType = input message: 'Choose one', ok: 'Proceed', 
                                        parameters: [choice(name: 'Type', choices: ['Full', 'Part', 'None'])]
                    echo "Selected scan type is ${scanType}"
                }
            }
        }
        stage('Goodbye') {
            steps {
                echo 'Hello World'
            }
        }
    }
}
